	// add image
	function add_image_upload_function(){	
		$('.blu_image_upload .blu_add_image img').each(function(){

			if($(this).attr('src') === ''){
				$(this).hide();
				$(this).parent().append('<span style="text-align: center; display: block; text-decoration: none; margin-top: 40px;">+ Add image</span>');
			}
		});
		$('.blu_image_upload .blu_add_image').click(function(e){

			e.preventDefault();
			var $image = $(this).parent().find('img'); 
			var $input = $(this).parent().find('.source');
			var $image_id = $(this).parent().find('.image_id');
		    wp.media.editor.send.attachment = function(props, attachment){
		    	$image.show();
		    	$image.parent().find('span').remove();
		    	$input.val(attachment.url);
		    	$image_id.val(attachment.id);
		    	$image.attr('src', attachment.url );
		    	$image.data('image-id', attachment.id );
		    	// console.log(attachment);
		    }
		    wp.media.editor.open(this);
		    return false;
		});
	}
	add_image_upload_function();
	
	// remove image
	$('.blu_image_upload .remove_image').click(function(e){
		e.preventDefault();

		if (confirm("Are you sure you want to delete the image?")) {
			var $image = $(this).parent().children('.blu_add_image').children('img'); 
			var $input = $(this).parent().children('input');
			var $source = $(this).parent().children('.source');

		    $input.val('');
		    $source.val('');
		    $image.hide();
		    // $image.attr('src', $image.data('placeholder') );
		}
		return false;
	});



