			fontselectorHTML += '</select>';
			fontselectorHTML += '<div id="bl_font_variants"><h3>Variants</h3><div></div></div>';
			fontselectorHTML += '<div id="bl_font_subsets"><h3>Subsets</h3><div></div></div>';
